mapper.select:
SELECT group_id,group_step,group_tab 
FROM reply
WHERE no=#{no}

mapper.update:
UPDATE reply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE reply SET 
depth=depth+1
WHERE no=#{no}

UPDATE reply SET
msg=#{msg}
WHERE no=#{no}

UPDATE reply SET
msg='<font color=red>관리자에 의해 삭제된 글입니다</font>'
WHERE no=#{no}

UPDATE reply SET
depth=depth-1
WHERE no=#{no}
